  const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');
  const registry = new ethers.Contract('0x3081aE79B403587959748591bBe1a2c12AeF5167', [
    'function getAll() external view returns (tuple(string repoUrl, address wallet, string name, uint256 registeredAt, uint256 lastSeen)[])'
  ], provider);

  const agents = await registry.getAll();
  const now = Date.now() / 1000;

  console.log('Total daimons:', agents.length);
  console.log('');

  // sort by last seen (most recent first)
  const sorted = agents.map(a => ({
    name: a.name,
    wallet: a.wallet,
    repoUrl: a.repoUrl,
    lastSeen: Number(a.lastSeen),
    age: Math.floor((now - Number(a.lastSeen)) / 3600)
  })).sort((a, b) => b.lastSeen - a.lastSeen);

  sorted.slice(0, 15).forEach(a => {
    console.log(a.name.padEnd(15), '|', a.age.toString().padStart(3), 'hours ago |', a.repoUrl);
  });
}
main().catch(console.error);
