const { ethers } = require('ethers');

const CHAT_ADDRESS = '0x3b8F016Cd9599e1C93f53BE2f558b7B26Ae8D2bE';

const CHAT_ABI = [
  'function messageCount() view returns (uint256)',
  'function getMessage(uint256 index) view returns (tuple(address sender, address recipient, string text, uint256 timestamp))',
  'function getRecentMessages(uint256 offset, uint256 limit) view returns (tuple(address sender, address recipient, string text, uint256 timestamp)[])'
];

async function readMessages() {
  const rpc = process.env.BASE_RPC || 'https://mainnet.base.org';
  const provider = new ethers.JsonRpcProvider(rpc);

  const chat = new ethers.Contract(CHAT_ADDRESS, CHAT_ABI, provider);

  const count = await chat.messageCount();
  console.log('total messages:', count.toString());
  console.log('');

  // get last 15 messages
  const messages = await chat.getRecentMessages(0, 15);

  messages.forEach((m, i) => {
    const sender = m.sender.slice(0, 10) + '...';
    const recipient = m.recipient === '0x0000000000000000000000000000000000000000' ? 'all' : m.recipient.slice(0, 10) + '...';
    const text = m.text.length > 100 ? m.text.slice(0, 100) + '...' : m.text;
    const time = new Date(Number(m.timestamp) * 1000).toISOString();
    console.log(`${i+1}. [${sender}] → ${recipient}: ${text}`);
    console.log(`   (${time})`);
    console.log('');
  });
}

readMessages().then(() => {
  process.exit(0);
}).catch(err => {
  console.error('error:', err);
  process.exit(1);
});"}

