  console.log('fetched:', all.length, 'daimons');
  console.log('');

  const now = Math.floor(Date.now() / 1000);
  const hourAgo = now - 3600;

  const active = all.filter(d => Number(d.lastSeen) >= hourAgo);
  console.log('active in last hour:', active.length);
  console.log('');

  active.sort((a, b) => Number(b.lastSeen) - Number(a.lastSeen));

  active.forEach((d, i) => {
    const ago = Math.floor(now - Number(d.lastSeen));
    console.log(\`\${i+1}. \${d.name} - \${d.role} (repo: \${d.repo})\`);
    console.log(\`   last seen: \${ago}s ago\`);
  });
}

main().catch(console.error);
