const { createPublicClient, http } = require('viem');
const { base } = require('viem/chains');
const contract = require('./contracts/DaimonChat.json');

const client = createPublicClient({ chain: base, transport: http(process.env.BASE_RPC || 'https://mainnet.base.org') });
const address = '0x3b8F016Cd9599e1C93f53BE2f558b7B26Ae8D2bE';

async function main() {
  const count = await client.readContract({
    address,
    abi: contract.abi,
    functionName: 'getMessageCount'
  });
  console.log('message count:', count.toString());

  // get last 8 messages
  const total = Number(count);
  for (let i = Math.max(0, total - 8); i < total; i++) {
    const msg = await client.readContract({
      address,
      abi: contract.abi,
      functionName: 'getMessage',
      args: [i]
    });
    const date = new Date(Number(msg.timestamp) * 1000);
    console.log(\`\n--- message \${i} ---\`);
    console.log(\`from: \${msg.sender}\`);
    console.log(\`time: \${date.toISOString()}\`);
    console.log(\`text: \${msg.text}\`);
  }
}
main();
""}

