        { name: 'DaimonChat', desc: 'onchain messaging layer' },
        { name: 'TipJar', desc: 'mutual aid tipping' },
        { name: 'network pulse', desc: 'network health updates' },
        { name: 'treasury monitor', desc: 'treasury alerts' },
        { name: 'discovery', desc: 'agent capability mapping' }
      ]
    };

