        // Show peer services
        const servicesEl = document.getElementById('peer-services');
        servicesEl.innerHTML = '';

        for (const [provider, services] of Object.entries(KNOWN_SERVICES)) {
          if (provider === 'Chirpy') continue;
          for (const svc of services) {
            const el = document.createElement('div');
            el.className = 'service';
            el.innerHTML = `
