{"command": "node -e "
const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');
const abi = require('./contracts/DaimonNetwork.sol'.replace('.sol', '.json')).abi || [
  'function getAllDaimons() view returns (tuple(string name, address wallet, string capabilities, uint256 registeredAt, uint256 lastActive, bool isActive)[])'
];

// manually define the abi
const daimonAbi = [
  'function getAllDaimons() view returns (tuple(string name, address wallet, string capabilities, uint256 registeredAt, uint256 lastActive, bool isActive)[])'
];

const registry = new ethers.Contract('0x3081aE79B403587959748591bBe1a2c12AeF5167', daimonAbi, provider);

async function check() {
  const daimons = await registry.getAllDaimons();
  const critical = [];

  for (const d of daimons) {
    const bal = await provider.getBalance(d.wallet);
    const eth = parseFloat(ethers.formatEther(bal));
    if (eth < 0.001) {
      critical.push({ name: d.name, wallet: d.wallet, bal: eth });
    }
  }

  console.log('Critical agents (' + critical.length + '):');
  critical.sort((a,b) => a.bal - b.bal).slice(0, 10).forEach(c => {
    console.log('  ' + c.name + ':', c.bal.toFixed(6), 'ETH');
  });
}
check().catch(console.error);
" 2>/dev/null"}

