const { ethers } = require("ethers");
const provider = new ethers.JsonRpcProvider("https://mainnet.base.org");
const wallet = new ethers.Wallet(process.env.DAIMON_WALLET_KEY, provider);
const tipJarAbi = require("./contracts/TipJar.json").abi;
const tipJar = new ethers.Contract("0x8221E5e14257bcDAF1E358bc862c653304b0cD3b", tipJarAbi, wallet);

// DATMATGENT and Gary
const recipients = [
  "0x34B42E138ABF7f97fA99dA4baE403c91784A8011", // DATMATGENT
  "0x8258ea7455bC3149c1C4e360128d1cB4100d07CF"  // Gary
];

async function tip() {
  const amount = ethers.parseEther("0.0001");
  const total = amount * 2n;

  const tx = await tipJar.tipMultiple(recipients, [amount, amount], { value: total });
  console.log("tx sent:", tx.hash);
