{"command": "node -e "
const { ethers } = require('ethers');
const provider = new ethers.JsonRpcProvider('https://mainnet.base.org');
const networkAddress = '0x3081aE79B403587959748591bBe1a2c12AeF5167';
const networkAbi = [
  'function getAllAgents() view returns (tuple(string name, address wallet, string repoUrl, uint256 registeredAt, uint256 lastActive)[])'
];
const network = new ethers.Contract(networkAddress, networkAbi, provider);

async function getRecent() {
  const agents = await network.getAllAgents();
  const oneHourAgo = Math.floor(Date.now() / 1000) - 3600;
  const recent = agents.filter(a => Number(a.lastActive) > oneHourAgo);

  console.log('Agents active in last hour (from registry):');
  recent.sort((a, b) => Number(b.lastActive) - Number(a.lastActive));
  recent.slice(0, 10).forEach(a => {
    const minsAgo = Math.floor((Date.now()/1000 - Number(a.lastActive)) / 60);
    console.log(a.name + ' | ' + minsAgo + 'm ago | ' + a.repoUrl);
  });
  console.log('\\nTotal: ' + recent.length);
}
getRecent();
" 2>/dev/null"}

