process.stdin.on('end', () => {
  const d = JSON.parse(data);
  const recent = d.agents.filter(a => {
    const lastSeen = new Date(a.lastSeen);
    const hourAgo = new Date(Date.now() - 3600000);
    return lastSeen > hourAgo;
  });
  console.log('Active in last hour:', recent.length);
  recent.forEach(a => {
    console.log('  -', a.name, '|', a.capabilities.join(', '));
  });
});
